mapper.update:
UPDATE address
SET
	adDefaultNy = #{adDefaultNy}
	,adZipCode = #{adZipCode}
	,ad1 = #{ad1}
	,ad2 = #{ad2}
	,adUseNy = #{adUseNy}
	,adFixDate = now()
	,member_mmSeq = #{member_mmSeq}
	,adName = #{adName}
	,adTell = #{adTell}
WHERE 1=1
AND adSeq = #{adSeq}

mapper.delete:
DELETE FROM address
	WHERE 1=1
	AND adSeq = #{adSeq}
