mapper.update:
update codegroup
set
	cgName = #{cgName}
	,cgUseNy = #{cgUseNy}
	,cgTurn = #{cgTurn}
	,cgDesc = #{cgDesc}
	,cgFixDate = now()
where 1=1
	and cgSeq = #{cgSeq}

mapper.delete:
delete from codegroup
where 1=1
	and cgSeq = #{cgSeq}

mapper.sql:
 	from
		codegroup a
	where 1=1
		and cgDelNy = #{shcgDelNy}
		and cgUseNy = #{shcgUseNy}

			and cgSeq = #{shValue}
			and cgName like concat('%',#{shValue},'%')
			and cgTurn like concat('%',#{shValue},'%')



			AND a.cgDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
			AND a.cgFixDate BETWEEN #{shDateStart} AND #{shDateEnd}
