mapper.insert:
INSERT INTO code (
    cdName,
    cdUseNy,
    cdTurn,
    cdDesc,
    delNy,
    cdDateTime,
    cdFixDate,
    codegroup_seq
	) 
VALUES (
    #{cdName}
    ,#{cdUseNy}
    ,#{cdTurn}
    ,#{cdDesc}
    ,0
    ,now()
    ,now()
    ,#{codegroup_seq}
	)

mapper.update:
update code as b 
left join codegroup as a
on b.codegroup_seq = a.cdseq
set
	b.cdName = #{cdName}
	,b.cdUseNy = #{cdUseNy}
	,b.cdTurn = #{cdTurn}
	,b.cdDesc = #{cdDesc}
	,b.cdFixDate = now()
	,b.codegroup_seq = #{codegroup_seq}
	,a.codeName = #{codeName}
	,a.seq as cdseq = #{cdseq}
where 1=1
	and b.seq = #{seq}
