mapper.insert:
INSERT INTO code (
    cdName,
    cdUseNy,
    cdTurn,
    cdDesc,
    delNy,
    cdDateTime,
    cdFixDate,
    codegroup_seq
	) 
VALUES (
    #{cdName}
    ,#{cdUseNy}
    ,#{cdTurn}
    ,#{cdDesc}
    ,0
    ,now()
    ,now()
    ,1
	)

mapper.update:
update code
set
	cdName = #{cdName}
	,cdUseNy = #{cdUseNy}
	,cdTurn = #{cdTurn}
	,cdDesc = #{cdDesc}
	,codegroup_seq = #{codegroup_seq}
	,cdFixDate = now()

where 1=1
	and seq = #{seq}
