mapper.update:
update code b 

set
	b.cdName = #{cdName}
	,b.cdUseNy = #{cdUseNy}
	,b.cdTurn = #{cdTurn}
	,b.cdDesc = #{cdDesc}
	,b.cdFixDate = now()
	,b.codegroup_seq = #{codegroup_seq}

where 1=1
	and b.cdSeq = #{cdSeq}

mapper.delete:
delete from code
where 1=1
	and cdSeq = #{cdSeq}

mapper.sql:
from
	code b
left join codegroup a
on a.cgSeq = b.codegroup_seq
where 1=1
and cdDelNy = #{shcdDelNy}
and cdUseNy = #{shcdUseNy}

	and cdSeq = #{shValue}
	and cgSeq = #{shValue}
	and cdName like concat('%',#{shValue},'%')	
	and cgName like concat('%',#{shValue},'%')
	and cdTurn = #{shValue}



	AND b.cdDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
	AND b.cdFixDate BETWEEN #{shDateStart} AND #{shDateEnd}
