mapper.update:
update eats
set
	name = #{name}
	,timeStart = #{timeStart}
	,timeEnd = #{timeEnd}
	,number = #{number}
	,site	= #{site}
	,pakingNy = #{pakingNy}
	,bookingNy = #{bookingNy}
	,freecallNy = #{freecallNy}
	,eatsDesc = #{eatsDesc}
	,date = #{date}
	,fixDate = #{fixDate}
where 1=1
	and seq = #{seq}
