mapper.update:
update education
set
	type
	,name = #{name}
	,pay = #{pay}
	,teacher = #{teacher}
	,classStart = #{classStart}
	,classEnd = #{classEnd}
	,eduStart = #{eduStart}
	,eduEnd = #{eduEnd}
	,place = #{place}
	,placeDesc = #{placeDesc}
	,date = #{date}
	,fixDate = #{fixDate}
	,user_seq = #{user_seq}
where 1=1
	and seq = #{seq}
