mapper.select:
SELECT mm.mmSeq ,mm.mmRealName ,mm.mmNickName ,mm.mmEmail ,mm.mmTell ,mm.mmBirthDay ,mm.mmDelNy ,mm.mmUseNy ,mm.mmDate ,mm.mmFixDate ,mm.mmDesc ,mm.mmGender FROM member mm where 1=1 and mmDelNy = #{shmmDelNy} and mmUseNy = #{shmmUseNy}
and mmSeq = #{shValue} and mmRealName like concat('%',#{shValue},'%') and mmNickName like concat('%',#{shValue},'%') and mmBirthDay like concat('%',#{shValue},'%') and mmEmail like concat('%',#{shValue},'%') and mmTell like concat('%',#{shValue},'%')
AND mm.mmDate BETWEEN #{shDateStart} AND #{shDateEnd} AND mm.mmFixDate BETWEEN #{shDateStart} AND #{shDateEnd}
ORDER BY mm.mmSeq DESC

mapper.update:
update member
set
	mmRealName = #{mmRealName}
	,mmUseNy = #{mmUseNy}
	,mmNickName = #{mmNickName}
	,mmDesc = #{mmDesc}
	,mmFixDate = now()
	,mmEmail = #{mmEmail}
	,mmTell = #{mmTell}
	,mmBirthDay = #{mmBirthDay}
	,mmDesc = #{mmDesc}
	,mmGender = #{mmGender}
where 1=1
	and mmSeq = #{mmSeq}

mapper.delete:
delete from member
where 1=1
	and mmSeq = #{mmSeq}
