mapper.insert:
INSERT INTO product(
	pdName
	,pdPrice
	,pdUseNy
	,pdDelNy
	,pdDate
	,pdFixDate
	,pdDesc
	,pdType
	,pdMeat
	)values(
	#{pdName}
	,#{pdPrice}
	,#{pdUseNy}
	,0
	,now()
	,now()
	,#{pdDesc}
	,#{pdType}
	,#{pdMeat}
	)

	SELECT last_insert_id()

mapper.update:
UPDATE product
SET
	pdName = #{pdName}
	,pdPrice = #{pdPrice}
	,pdUseNy = #{pdUseNy}
	,pdDesc = #{pdDesc}
	,pdType = #{pdType}
	,pdMeat = #{pdMeat}
	,pdFixDate = now()
WHERE 1=1
	AND pdSeq = #{pdSeq}

mapper.delete:
DELETE FROM product
WHERE 1=1
AND pdSeq = #{pdSeq}

mapper.sql:
	FROM
	 product pd
 	LEFT JOIN wishlist wh
 	ON pd.pdSeq = wh.product_pdSeq
 	LEFT JOIN insertuploaded
 	ON pSeq = pd.pdSeq
	WHERE 1=1
	and defaultNy = 1

	and pdDelNy = #{shpdDelNy}
	and pdUseNy = #{shpdUseNy}

		AND pdSeq = #{shValue}
		AND pdName LIKE CONCAT('%',#{shValue},'%')
		AND pdPrice LIKE CONCAT('%',#{shValue},'%')
		AND pdType LIKE CONCAT('%',#{shValue},'%')
		AND pdMeat LIKE CONCAT('%',#{shValue},'%')


		AND pd.pdDate BETWEEN #{shDateStart} AND #{shDateEnd}
		AND pd.pdFixDate BETWEEN #{shDateStart} AND #{shDateEnd}


    	AND pd.pdPrice BETWEEN #{shLowPrice} AND #{shTopPrice}


	and pdMeat = #{shPdMeat}
	and pdType = #{shPdType}
	AND pdName LIKE CONCAT('%', #{shName}, '%')

mapper.delete:
DELETE FROM wishlist
WHERE 1=1
AND whSeq = #{whSeq}
